import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const Edit = () => {
    const navigate = useNavigate();
    const { id } = useParams(); // Get the project ID from the URL
    // State for project details
    const [inpval, setINP] = useState({
        steps: "",
        materials: "",
        progress: ""
    });

    // Fetch project data by ID and populate the state
    const getdata = async () => {
        try {
            const res = await fetch(`http://localhost:8003/getuser/${id}`, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            });

            const data = await res.json();
            if (res.status === 200) {
                setINP(data);
            } else {
                console.error("Error fetching data");
            }
        } catch (error) {
            console.error("Error:", error);
        }
    };

    // Update state on input change
    const setdata = (e) => {
        const { name, value } = e.target;
        setINP((prev) => ({
            ...prev,
            [name]: value
        }));
    };

    // Update project details
    const updateProject = async (e) => {
        e.preventDefault();
        const { steps, materials, progress } = inpval;

        try {
            const res = await fetch(`http://localhost:8003/updateuser/${id}`, {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ steps, materials, progress })
            });

            const data = await res.json();

            if (res.status === 200) {
                navigate("/"); // Redirect to Home page after update
            } else {
                alert("Failed to update project. Please check the inputs.");
            }
        } catch (error) {
            console.error("Error updating project:", error);
        }
    };

    // Fetch project data on component mount
    useEffect(() => {
        getdata();
    }, []);

    return (
        <div className="container">
            <form className="mt-4">
                <div className="row">
                    <div className="mb-3 col-lg-12 col-md-12 col-12">
                        <label className="form-label">Steps</label>
                        <textarea
                            name="steps"
                            value={inpval.steps}
                            onChange={setdata}
                            className="form-control"
                            rows="4"
                            required
                        ></textarea>
                    </div>
                    <div className="mb-3 col-lg-12 col-md-12 col-12">
                        <label className="form-label">Materials</label>
                        <textarea
                            name="materials"
                            value={inpval.materials}
                            onChange={setdata}
                            className="form-control"
                            rows="4"
                            required
                        ></textarea>
                    </div>
                    <div className="mb-3 col-lg-6 col-md-6 col-12">
                        <label className="form-label">Progress (%)</label>
                        <input
                            type="number"
                            name="progress"
                            value={inpval.progress}
                            onChange={setdata}
                            className="form-control"
                            min="0"
                            max="100"
                            required
                        />
                    </div>

                    <button type="submit" onClick={updateProject} className="btn btn-primary">
                        Update Project
                    </button>
                </div>
            </form>
        </div>
    );
};

export default Edit;
