import React, { useState, useEffect, useContext } from "react";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import CreateIcon from "@mui/icons-material/Create";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import { NavLink } from "react-router-dom";
import { adddata, deldata, updatedata } from "./context/ContextProvider";

const Home = () => {
    const [getuserdata, setUserdata] = useState([]);
    const [showSuccessAlert, setShowSuccessAlert] = useState(false);
    const [showUpdateAlert, setShowUpdateAlert] = useState(false);
    const [showDeleteAlert, setShowDeleteAlert] = useState(false);
    const { udata, setUdata } = useContext(adddata);
    const { updata, setUPdata } = useContext(updatedata);
    const { dltdata, setDLTdata } = useContext(deldata);

    const getdata = async () => {
        const res = await fetch("http://localhost:8003/getdata", {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },
        });
        const data = await res.json();
        if (res.status === 422 || !data) {
            console.error("Error fetching data");
        } else {
            setUserdata(data);
        }
    };

    useEffect(() => {
        getdata();
    }, []);

    const deleteuser = async (id) => {
        const res2 = await fetch(`http://localhost:8003/deleteuser/${id}`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
            },
        });
        const deletedata = await res2.json();
        if (res2.status === 422 || !deletedata) {
            console.error("Error deleting user");
        } else {
            setDLTdata(deletedata);
            getdata();
            setShowDeleteAlert(true);
            setTimeout(() => setShowDeleteAlert(false), 3000);
        }
    };

    useEffect(() => {
        if (udata) {
            setShowSuccessAlert(true);
            setTimeout(() => {
                setShowSuccessAlert(false);
                setUdata(null);
            }, 3000);
        }
        if (updata) {
            setShowUpdateAlert(true);
            setTimeout(() => {
                setShowUpdateAlert(false);
                setUPdata(null);
            }, 3000);
        }
    }, [udata, updata]);

    return (
        <>
            {showSuccessAlert && udata && (
                <div className="alert alert-success" role="alert">
                    <strong>{udata.name}</strong> added successfully!
                </div>
            )}
            {showUpdateAlert && updata && (
                <div className="alert alert-success" role="alert">
                    <strong>{updata.name}</strong> updated successfully!
                </div>
            )}
            {showDeleteAlert && dltdata && (
                <div className="alert alert-danger" role="alert">
                    <strong>{dltdata.name}</strong> deleted successfully!
                </div>
            )}

            <div className="mt-5 container">
                <div className="add_btn mt-2 mb-2">
                    <NavLink to="/register" className="btn btn-primary">
                        Add data
                    </NavLink>
                    <button
                        onClick={downloadPDF}
                        className="btn btn-secondary ms-2"
                    >
                        Download PDF
                    </button>
                </div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Username</th>
                            <th>Email</th>
                            <th>Job</th>
                            <th>Mobile</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {getuserdata.map((element, id) => (
                            <tr key={id}>
                                <td>{id + 1}</td>
                                <td>{element.name}</td>
                                <td>{element.email}</td>
                                <td>{element.work}</td>
                                <td>{element.mobile}</td>
                                <td>
                                    <NavLink to={`view/${element._id}`}>
                                        <button className="btn btn-success">
                                            <RemoveRedEyeIcon />
                                        </button>
                                    </NavLink>
                                    <NavLink to={`edit/${element._id}`}>
                                        <button className="btn btn-primary">
                                            <CreateIcon />
                                        </button>
                                    </NavLink>
                                    <button
                                        className="btn btn-danger"
                                        onClick={() => deleteuser(element._id)}
                                    >
                                        <DeleteOutlineIcon />
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );

    function downloadPDF() {
        fetch("http://localhost:8003/downloadpdf")
            .then(response => {
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                return response.blob(); // Convert the response to a Blob
            })
            .then(blob => {
                const url = window.URL.createObjectURL(blob);
                const a = document.createElement("a");
                a.href = url;
                a.download = "StaffSphere.pdf"; // Set the file name
                document.body.appendChild(a);
                a.click(); // Trigger the download
                a.remove(); // Clean up
            })
            .catch(error => {
                console.error("Error downloading PDF:", error);
                alert("Error downloading PDF. Please try again later.");
            });
    }


};

export default Home;
