import React, { useState,useEffect} from 'react';  
import { NavLink } from 'react-router-dom'; 
import { useNavigate } from 'react-router-dom'; 

const Navbaar = () => {  
    const [searchTerm, setSearchTerm] = useState(''); // State for search input  
    const [isAuthenticated, setIsAuthenticated] = useState(false); // State for authentication
    const navigate = useNavigate(); 
    useEffect(() => {
        // Check if the user is authenticated by looking for a token in localStorage
        const token = localStorage.getItem('token');
        setIsAuthenticated(!!token);
      }, []);

    const handleSearchSubmit = (e) => {  
        e.preventDefault(); // Prevent default form submission  
        if (searchTerm) {  
            // Navigate to /search with query in the URL
            window.location.href = `/search?query=${encodeURIComponent(searchTerm)}`; 
            setSearchTerm(''); // Clear input
        }
    };  
    const handleLogout = () => {
        // Remove token from localStorage
        localStorage.removeItem('token');
        setIsAuthenticated(false);
        navigate('/login'); // Redirect to login page
      };
    return (  
        <header>  
            <nav className="navbar navbar-expand-lg navbar-light" style={{ background: 'linear-gradient(to right, #009688, #80cbc4)' }}>  
                <div className="container-fluid">  
                    <NavLink className="navbar-brand" to="/">  
                        <span style={{ fontWeight: 'bold', fontSize: '1.5em', color: 'white' }}>S</span>  
                        <span style={{ color: 'white' }}>TAFF</span>  
                        <span style={{ fontWeight: 'bold', fontSize: '1.5em', color: 'white' }}>S</span>  
                        <span style={{ color: 'white' }}>PHERE</span>  
                    </NavLink>  
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">  
                        <form className="d-flex" onSubmit={handleSearchSubmit}>  
                            <input   
                                className="form-control me-2"   
                                type="search"   
                                placeholder="Search"   
                                aria-label="Search"   
                                value={searchTerm}  
                                onChange={(e) => setSearchTerm(e.target.value)}  
                            />  
                            <button className="btn btn-outline-success" type="submit">Search</button>  
                        </form>  
                    </div>  
                </div>  
            </nav>  
        </header>  
    );  
};  

export default Navbaar;
